import { useState } from "react";

const INITIAL_USER = {
  name: {
    first: "",
    last: ""
  },
  email: ""
};

const SetComplexObject = () => {
  const [user, setUser] = useState(INITIAL_USER);

  return (
    <div>
      <h5>Your Name Is: {user.name.first} {user.name.last}</h5>
      <h6>Your email is: {user.email}</h6>
      <form>
        <input
          placeholder="Enter First Name"
          onChange={e => setUser({ ...user, name: { ...user.name, first: e.target.value } })}
        />
        <input
          placeholder="Enter Last Name"
          onChange={e => setUser({ ...user, name: { ...user.name, last: e.target.value } })}
        />
        <input
          placeholder="Enter Email"
          onChange={e => setUser({ ...user, email: e.target.value })}
        />
      </form>
    </div>
  );
};

export default SetComplexObject;

import { useState } from "react";

export const SetArray = () => {
  const INITIAL_TODO = { todo: "" };
  const [task, setTask] = useState(INITIAL_TODO);
  const [tasks, setTasks] = useState([]);

  const createNewTask = e => {
    e.preventDefault();
    setTasks([...tasks, task]);
    return setTask(INITIAL_TODO);
  };

  return (
    <div className="d-flex justify-content-center mt-4">
      <div>
        <h5>Task: <span className="fw-light"> {task.todo}</span></h5>
        <form className="col-12 border p-2 rounded">
          <div className="input-group">
            <button
              disabled={!task.todo}
              onClick={createNewTask}
              className="input-group-text"
              id="inputGroup-sizing-default"
            >
              Create
            </button>
            <input
              onChange={e => setTask({ ...task, todo: e.target.value })}
              value={task.todo}
              type="text"
              className="form-control"
              aria-label="Sizing example input"
              aria-describedby="inputGroup-sizing-default"
            />
          </div>
        </form>
        <ul>
          {tasks.map((todo, index) => (
            <li key={index}>
              {index + 1}. {todo.todo}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

