          type="text"
          placeholder="Enter Last Name"
          onChange={e => setUser({...user, name: {...user.name, last: e.target.value}})}
        />

        <input
          type="text"
          placeholder="Enter Email"
          onChange={e => setUser({...user, email: e.target.value})}
        />
      </form>
    </div>
  );
};

export default SetComplexObject;

