  );

  const handleLike = async () => {
    setLike(prev => !prev);
    await handleLikeCard(cardId);
    onLike();
  };

  return (
    <CardActions>
      {user && (
        <IconButton aria-label="add to favorites" onClick={handleLike}>
          <FavoriteIcon color={isLiked ? "error" : "inherit"} />
        </IconButton>
      )}
      <IconButton
        onClick={() => handleDialog("open")}
        aria-label="delete card"
        color="error"
      >
        <DeleteIcon />
      </IconButton>
      <CardDeleteDialog
        isDialogOpen={isDialogOpen}
        onChangeDialog={handleDialog}
        onDelete={handelDeleteCard}
      />
    </CardActions>
  );
};

const Card = ({ card, onDelete, onLike }) => {
  const navigate = useNavigate();
  return (
    <MuiCard sx={{ minWidth: 280 }}>
      <CardActionArea
        onClick={() => navigate(`${ROUTES.CARD_INFO}/${card._id}`)}>
        <CardHead image={card.image} />
        <CardBody card={card} />
      </CardActionArea>
      <CardActionBar 
        onDelete={onDelete} 
        cardId={card._id} 
        cardUserId={card.user_id}
        cardLikes={card.likes}
        onLike={onLike}
      />
    </MuiCard>
  );
};

Card.propTypes = {
  card: cardType.isRequired,
  onDelete: func.isRequired,
  onLike: func.isRequired,
};

export default React.memo(Card);

const Cards = ({ cards, onDelete, onLike }) => {
  if (!cards.length)
    return (
      <Typography m={2}>
        Oops... it seems there are no business cards to display
      </Typography>
    );

  return (
    <Grid container spacing={2} pb={2}>
      {cards.map((card, i) => (
        <Grid item xs={12} sm={6} md={4} lg={3} key={i}>
          <Card card={card} onDelete={onDelete} onLike={onLike} />
        </Grid>
      ))}
    </Grid>
  );
};

Cards.propTypes = {
  cards: arrayOf(cardType).isRequired,
  onDelete: func.isRequired,
  onLike: func.isRequired,
};

export default Cards;

const CardsFeedback = ({ isLoading, error, cards, onDelete, onLike }) => {
  if (isLoading) return <Spinner />;
  if (error) return <Error errorMessage={error} />;
  if (cards && !cards.length)
    return (
      <div>
        {""}
        Oops, there are no business cards in the database that match the
        parameters you entered
      </div>
    );
  if (cards) return <Cards cards={cards} onDelete={onDelete} onLike={onLike} />;
  return null;
};

CardsFeedback.propTypes = {
  isLoading: bool.isRequired,
  error: string,
  cards: arrayOf(object),
  onDelete: func.isRequired,
  onLike: func.isRequired,
};

CardsFeedback.defaultProps = {
  onLike: () => {},
};

export default React.memo(CardsFeedback);

import { useCallback } from "react";
import { useCards } from "../hooks/useCards";
import { Container } from "@mui/material/Container";
import PageHeader from "../../components/PageHeader";
import { useEffect } from "react";
import CardsFeedback from "../components/CardsFeedback";

const FavCardsPage = () => {
  const { value, ...rest } = useCards();
  const { isLoading, error, cards } = value;
  const { handleDeleteCard, handleGetFavCards } = rest;

  useEffect(() => {
    handleGetFavCards();
  }, []);

  const onDeleteCard = useCallback(async cardId => {
    await handleDeleteCard(cardId);
    await handleGetFavCards();
  }, [handleDeleteCard, handleGetFavCards]);

  return (
    <Container>
      <PageHeader 
        title="Favorite Cards" 
        subtitle="Here you can find your favorite business cards" 
      />
      <CardsFeedback 
        isLoading={isLoading}
        error={error}
        cards={cards}
        onDelete={onDeleteCard}
      />
    </Container>
  );
};

export default FavCardsPage;

const handleGetFavCards = useCallback(async () => {
  try {
    setLoading(true);
    const cards = await handleGetCards();
    const favCards = cards.filter(
      card => !!card.likes.find(id => id === user._id)
    );
    requestStatus(false, null, favCards);
  } catch (error) {
    requestStatus(false, error, null);
  }
}, [user]);

const [query, setQuery] = useState("");
const [filteredCards, setFilter] = useState(null);
const [searchParams] = useSearchParams();

useEffect(() => {
  setQuery(searchParams.get("q") || "");
}, [searchParams]);

useEffect(() => {
  if (cards) {
    setFilter(
      cards.filter(
        card =>
          card.title.toLowerCase().includes(query.toLowerCase()) ||
          card.bizNumber.includes(query)
      )
    );
  }
}, [cards, query]);

const value = useMemo(() => {
  return { cards, card, isLoading, error, filteredCards };
}, [cards, card, isLoading, error, filteredCards]);

return {
  value,
  handleGetCard,
  handleDeleteCard,
  handleGetCards,
  handleCreateCard,
  handleGetMyCards,
  handleUpdateCard,
  handleLikeCard,
  setCards,
  handleGetFavCards
};
