const FIRST_NUM = document.getElementById('firstNum') as HTMLInputElement;
const SECOND_NUM = document.getElementById('secondNum') as HTMLInputElement;
const BTN = document.getElementById('btn') as HTMLButtonElement;
const RESULT = document.getElementById('result') as HTMLDivElement;

const sum = (firstNum: number, secondNum: number): number => {
    return firstNum + secondNum;
};

const printNum = (): void => {
    const result = sum(Number(FIRST_NUM.value), Number(SECOND_NUM.value));
    RESULT.innerHTML = String(result);
};

BTN.addEventListener('click', printNum);

