"use strict";
const FIRST_NUM = document.getElementById('firstNum');
const SECOND_NUM = document.getElementById('secondNum');
const BTN = document.getElementById('btn');
const RESULT = document.getElementById('result');

const sum = (firstNum, secondNum) => {
    return firstNum + secondNum;
};

const printNum = () => {
    const result = sum(Number(FIRST_NUM.value), Number(SECOND_NUM.value));
    RESULT.innerHTML = String(result);
};

BTN.addEventListener('click', printNum);

